import React, { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import './App.css';
import NavBar from "./NavBar/NavBar";
import Footer from './Footer/Footer';
import SearchResult from './Search/SearchResult'
import ProductDetailPage from "./DetailProductPage/ProductDetailPage"
import Checkout from "./Cart/Checkout"
import Product from './Product/Product';
import Main from './Main';

function App() {
  let [products, setProducts] = useState([]);
  useEffect(() => {

    getProducts();
  },[]);
  
  const getProducts = async() => {
    try {
      const response = await axios.get('/products')
      const responseData = response.data;
  
      // responseData = responseData.json();
      // responseData = responseData.slice(0,30);
      const productsData = [];
      for (const key in responseData) {
        productsData.push({
          id: key,
          seq: responseData[key].seq,
          name: responseData[key].name,
          asin: responseData[key].asin,
          price: responseData[key].price,
          buylink: responseData[key].buylink,
          imglink: responseData[key].imglink,
          category: responseData[key].category,
        });
      }
      setProducts(productsData);
      console.log(productsData);
    } catch(err) {
      console.log("Error >>",err)
    }
  };
  
  return (
    <BrowserRouter>
      <NavBar />
      <div style={{ paddingTop: '69px', minHeight: 'calc(100vh - 80px)' }}>
        <Routes>
          <Route exact path="/">
            {products.map(function(product,i){
              return(
                <Main product={Product[i]} i={i} key= {i}/>
              )
            })}
          </Route>
          <Route path="/search" element={<SearchResult/>} />
          <Route path="/products/:name" exact element={<ProductDetailPage product={Product}/>} />
          {/* <Route path="/product" element={<ProductDetailPage/>} /> */}
          <Route path="/cart" element={<Checkout/>} />
        </Routes>
      </div>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
