import Card from '../Search/card';

// const BASE_URL = "";

const Product = (props) => {

    const product = {
      id : props.id,
      seq: props.seq,
      name: props.name,
      asin: props.asin,
      price: props.price,
      buylink : props.buylink,
      imglink : props.imglink,
      category : props.category
    };
    return (
      <Card>
          <div> title={props.name}, description={`$${props.price}`} </div>
      </Card>
    )
