import Card from '../Search/card';

// const BASE_URL = "";

const Product = (props) => {

    const product = {
      id : props.id,
      seq: props.seq,
      name: props.name,
      asin: props.asin,
      price: props.price,
      buylink : props.buylink,
      imglink : props.imglink,
      category : props.category
    };
    return (
      <Card>
        <div>
          <img src = {props.imglink} width = "100%" height = "100%"/>
       <h4>{props.name}</h4>
       <p>{props.category}</p>
       <p>{props.price}</p>
        </div>
      </Card>
    )
}


