import React, { useEffect, useState } from "react";
import { useParams, useHistory } from 'react-router-dom'
import axios from "axios";
import ProductInfo from "./Sections/ProductInfo";
import ProductImage from "./Sections/ProductImage";
import { Row, Col } from "antd";
import Product from "../Product/Product";
import Value from "./Value";

 
//왜 product를 못받아오는거지?!!!!!!!!!????????????????????????????????????????????????????????!?!??

function ProductDetailPage() {
  // let productName = props.match.params.name;
  // let name  = useParams();
  // let matchedproduct = props.name.find(function(product){
  //   return props.name == name;
  // });

  // console.log(name);

  const [products, setProducts] = useState([]);

  // let product = props.Products.find(function(product) {
  //   return product.name = productName;
    
  // })
  
  useEffect(() => {

    getProductByName();
  }, []);

    const getProductByName = () => {
        axios.get('/products/name?name=Umbra%20Mini%20Waste%20Can%201-1/2%20Gallon%20with%20Swing%20Lid,%20White')
        .then((res)=> {
          console.log(res.data);
          console.log(res.data[0]);
          setProducts(res.data)});
      }

      const Products = products.map(product =>
        <Product
            key = {product.id}
            seq = {product.seq}
            name = {product.name}
            asin = {product.asin}
            price = {product.price}
            buylink = {product.buylink}
            imglink = {product.imglink}
            category = {product.category}
        />
    )

  return (
    <div style={{ width: "100%", padding: "3rem 4rem" }}>
      <br />
      <div stye={{ display: "flex", justifyContent: "center" }}>
            <h1>
              {products}
            </h1>
        </div>
      <br />
      <Row gutter={[16, 16]}>
        <Col lg={12} sm={24}>
          {/* ProductImage */}

          {/* <ProductImage detail={product}/> */}
        </Col>

        <Col lg={12} sm={24}>
          {/* ProductInfo */}

          <ProductInfo detail={product}/>
        </Col>
      </Row>
    </div>
  );
}

export default ProductDetailPage;
