          }
          setProduct(productsData);
          console.log(productsData);
        } catch(err) {
          console.log("Error >>",err)
        }
        console.log(product);
      };



  return (
    <div style={{ width: "100%", padding: "3rem 4rem" }}>
      <br />
      <div stye={{ display: "flex", justifyContent: "center" }}>
          <h1>{product[0].name}아</h1>
        </div>
      <br />
      <Row gutter={[16, 16]}>
