import React, { useEffect, useState } from "react";
import axios from "axios";
import {Col, Row } from "antd";
import {RocketOutlined} from '@ant-design/icons'
import SearchBar from "../NavBar/Sections/SearchBar";
import Product from "../NavBar/Sections/SearchBar";
import { Input } from "antd";
import { search } from "core-js/fn/symbol";

const { Search } = Input;

function SearchResult() {
    const [products, setProducts] = useState([]);
    const [Skip, setSkip] = useState(0);
    const [Limit, setLimit] = useState(8);
    const [search, setSearch] = useState("");
      
    const searchHandler = (event) => {
        event.preventDefault();
        setSearch(event.currentTarget.value);
    };

    const onSearch = (e) => {
        e.preventDefault();
        if (search === null || search ==='') {
            axios.get('/products')
            .then((res)=> {setProducts(res.data)});
            console.log(products);
        }else {
            axios.get('/products/keyword',{
                params: {
                  keyword: SearchTerm
                }
            }).then((res)=> {setProducts(res.data)});
            console.log(products);
        }
    }
    // const searchProducts = async() => {
    //     try {
    // const response = await axios.get('/products/keyword?keyword=',
    // {params: {}})
    // const responseData = response.data;
    
    // // responseData = responseData.json();
    // // responseData = responseData.slice(0,30);
    // const productsData = [];
    // for (const key in responseData) {
    //   productsData.push({
    //     id: key,
    //     seq: responseData[key].seq,
    //     name: responseData[key].name,
    //     asin: responseData[key].asin,
    //     price: responseData[key].price,
    //     buylink: responseData[key].buylink,
    //     imglink: responseData[key].imglink,
    //     category: responseData[key].category,
    //   });
    // }
    // setProducts(productsData);
    // console.log(productsData);
    // } catch(err) {
    // console.log("Error >>",err)
    // }
    // };

    // const updateSearchTerm = (newSearchTerm) =>{
    //   setSearchTerm(newSearchTerm)
      
    //   setProducts();
    // }
    
    // useEffect(() => {
        
    //     searchProducts();
    // },[]);
    const loadMoreHandler = () => {
        let skip = Skip + Limit;
        console.log(skip);
        setSkip(skip);
    };
  


return (
  <div style={{ width: "75%", margin: "3rem auto" }}>
  <div style={{ textAlign: "center" }}>
    <h2>
      {" "}
      HomeMaker <RocketOutlined />
    </h2>
  </div>
  <br />
  <br />
  {/* Filter */}
  <div style={{display: "flex", justifyContent : "center", padding: "30px"}}>
  {/* 검색창에 입력하는 도중 바뀌는건 어떻게? */}    
      <form onSubmit={e => searchHandler(e)}>
        <input
          type="text"
          value={search}
          placeholder="input search text"
          onChange={searchHandler} />
      </form>
  </div>
  {/* Search */}
  {/* Card */}
  
  <Row gutter={[16, 16]}>{products.slice(0,Limit+Skip)}</Row>

  <br />
     <div style={{ display: "flex", justifyContent: "center" }}>
          <button onClick={loadMoreHandler}>더보기</button>
    </div>	
</div>

)
}


export default SearchResult
