import React, { useEffect, useState } from "react";
import { useParams, useHistory } from 'react-router-dom'
import axios from "axios";
import ProductInfo from "./Sections/ProductInfo";
import ProductImage from "./Sections/ProductImage";
import { Row, Col } from "antd";
import Product from "../Product/Product";
import Value from "./Value";
import { Button, Descriptions } from "antd";

 
//왜 product를 못받아오는거지?!!!!!!!!!????????????????????????????????????????????????????????!?!??

function ProductDetailPage(name) {
  // let productName = props.match.params.name;
  // let productName  = useParams();
  // let matchedproduct = props.name.find(function(product){
  //   return props.name == name;
  // });

  // console.log(name);

  const [products, setProducts] = useState([]);

  // let product = props.Products.find(function(product) {
  //   return product.name = productName;
    
  // })
  console.log(name);
  
  useEffect(() => {

    getProductByName();
  }, [name]);


    const getProductByName = () => {
        axios.get('/products/name?name='+name)
        .then((res)=> {
          console.log(res.data);
          console.log(res.data[0]);
          setProducts(res.data)});
      }

      const Products = products.map(product =>
        <Value
            key = {product.id}
            seq = {product.seq}
            name = {product.name}
            asin = {product.asin}
            price = {product.price}
            buylink = {product.buylink}
            imglink = {product.imglink}
            category = {product.category}
        />
    )

  return Products;
}
  
function Path() {

  const{name} = useParams();
  const Products = ProductDetailPage(name);

  return (<>
    <div>
        {Products}
    </div>
  </>);
}
export default Path;
